import React, { useState } from 'react';
import CustomCheckbox from './CustomCheckbox'; // Импорт вашего компонента CustomCheckbox

const YourComponent = ({ faculties }) => {
  const [checkboxes, setCheckboxes] = useState([]);

  const handleCheckboxChange = (facultyId, directionId) => {
    setCheckboxes(prevCheckboxes => {
      const updatedCheckboxes = [...prevCheckboxes];
      const index = updatedCheckboxes.findIndex(
        item => item.facultyId === facultyId && item.directionId === directionId
      );
      updatedCheckboxes[index].isChecked = !updatedCheckboxes[index].isChecked;
      return updatedCheckboxes;
    });
  };

  return (
    <div className="flex-col text-left w-[100%] pr-[2%] pl-[4%]">
      {faculties.map(faculty => (
        <div key={faculty.id}>
          <button className="flex my-2 justify-between w-[90%]">
            <span>{faculty.name}</span>
            <img className="w-[20px]" src="CaretDown.svg" alt="caret icon" />
          </button>
          <div className="flex-col items-center mx-5 my-5">
            {faculty.directions.map(direction => (
              <CustomCheckbox
                key={direction.id}
                label={direction.name}
                isChecked={checkboxes.some(
                  checkbox =>
                    checkbox.facultyId === faculty.id &&
                    checkbox.directionId === direction.id &&
                    checkbox.isChecked
                )}
                onChange={() => handleCheckboxChange(faculty.id, direction.id)}
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default YourComponent;
