import { useEffect, useState } from 'react';
import AddModal from '../AddModal/AddModal';
import Navbar from '../Navbar';
import getCroppedImg64 from '../getImage64';
import Cropper from 'react-easy-crop';

function AdminPage() {
  const [students, setStudents] = useState([]);
  console.log(students);
  const [selectAll, setSelectAll] = useState(false);
  const [selectedStudents, setSelectedStudents] = useState([]);
  console.log(selectedStudents);

  const toggleCheckbox = studentId => {
    if (selectedStudents.includes(studentId)) {
      setSelectedStudents(selectedStudents.filter(id => id !== studentId));
    } else {
      setSelectedStudents([...selectedStudents, studentId]);
    }
  };

  const toggleSelectAll = () => {
    if (selectAll) {
      // Если уже выбраны все, снимаем выбор со всех студентов
      setSelectedStudents([]);
    } else {
      // Иначе выбираем всех студентов
      setSelectedStudents(students.map(student => student._id));
    }
    // Инвертируем состояние выбора всех чекбоксов
    setSelectAll(!selectAll);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4000/service');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        // Фильтрация пустых или неполных записей
        const filteredStudents = data.students.filter(student => student.name);
        setStudents(filteredStudents);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    return () => {
      // Cancel the request here if needed
    };
  }, []);
  const [isChecked, setIsChecked] = useState(true);

  console.log(isChecked);

  const [searchValue, setSearchValue] = useState('');

  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchValue.toLowerCase())
  );

  const handleSearchChange = event => {
    setSearchValue(event.target.value);
  };

  const deleteStudents = async () => {
    try {
      const response = await fetch('http://localhost:4000/service/only', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: selectedStudents }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to delete students');
      }
      const data = await response.json();
      console.log(data);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const [addModal, setAddModal] = useState(false);

  useEffect(() => {
    // Проверяем значение avatar и устанавливаем соответствующий стиль для body
    if (addModal) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'visible';
    }
  }, [addModal]);

  return (
    <>
      {addModal && <AddModal setAddModal={setAddModal} />}
      <Navbar />

      <div className="App w-[100%] ">
        <div className="relative">
          <img src="Chesu.png" className="w-full h-auto" alt="Chesu" />
          <div className="absolute top-0 left-[5%] w-[52%] h-full flex flex-col justify-center items-left">
            <span className="text-white text-left text-3xl font-extrabold font-manrope mb-4">
              Портфолио студентов Чеченского государственного университет им. А.А.
              Кадырова
            </span>
            <span className="text-white w-[60%] text-left text-[15px]">
              Данный сервис предназначен для работодателей, которые ищут для найма
              студентов-выпускников нашего ВУЗа
            </span>
          </div>
        </div>
        <div class="w-[355px] h-[1020px] pl-[50px] pr-[30px] pt-5 pb-[30px] bg-white flex-col justify-start items-start gap-5 inline-flex">
          <div class="self-stretch h-32 flex-col justify-start items-start gap-[35px] flex">
            <div class="self-stretch justify-start items-center gap-[90px] inline-flex">
              <div class="text-gray-900 text-xl font-bold font-['Manrope']">Фильтры</div>
              <div class="text-right text-blue-500 text-[13px] font-medium font-['Manrope']">
                Очистить все
              </div>
            </div>
            <div class="self-stretch h-[66px] flex-col justify-start items-start gap-3 flex">
              <div class="flex-col justify-start items-start gap-3 flex">
                <div class="text-gray-900 text-[15px] font-bold font-['Manrope']">
                  Факультет/Направление{' '}
                </div>
                <div class="w-[275px] pl-[15px] pr-[70px] py-[9px] bg-slate-50 rounded-[7px] justify-center items-center gap-2.5 inline-flex">
                  <div class="w-[13px] h-[13px] relative"></div>
                  <div class="text-gray-500 text-xs font-normal font-['Manrope']">
                    Найти нужное направление{' '}
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div class="w-[345px] h-[0px] origin-top-left rotate-180 border border-gray-500"></div>
          <div class="self-stretch h-[238px] flex-col justify-between items-start flex">
            <div class="w-[275px] justify-between items-center inline-flex">
              <div class="w-[249px] text-gray-900 text-[13px] font-medium font-['Manrope']">
                Агротехнологический институт
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Биолого-химический факультет
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Факультет географии и геоэкологии
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Факультет иностранных языков
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Филологический факультет
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Исторический факультет
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Медицинский институт
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Факультет государственного   управления
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Институт математики, физики   и информационных технологий
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
          </div>
          <div class="self-stretch h-[250px] px-2.5 flex-col justify-start items-start gap-2.5 flex">
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Бизнес-информатика
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Инфокоммуникационные технологии и системы связи
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Информационная безопасность
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Математика
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Прикладная математика и информатика
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Программная инженерия
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Радиофизика
              </div>
            </div>
            <div class="w-[232px] justify-start items-center gap-2.5 inline-flex">
              <div class="w-[18px] h-[18px] rounded-[5px] border border-neutral-300"></div>
              <div class="grow shrink basis-0 text-gray-500 text-[13px] font-medium font-['Manrope']">
                Физика
              </div>
            </div>
          </div>
          <div class="self-stretch h-[41px] flex-col justify-start items-start gap-[5px] flex">
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Институт экономики и финансов
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
            <div class="w-[275px] justify-start items-center gap-2.5 inline-flex">
              <div class="grow shrink basis-0 text-gray-900 text-[13px] font-medium font-['Manrope']">
                Юридический факультет
              </div>
              <div class="w-4 h-4 relative origin-top-left -rotate-90"></div>
            </div>
