import { useState } from 'react';
import AddModal from '../AddModal/AddModal';
import Navbar from '../Navbar';
import getCroppedImg64 from '../getImage64';
import Cropper from 'react-easy-crop';

function AdminPage({ setAddModal }) {
  const [rotation, setRotation] = useState(0);
  const [imageSrc, setImageSrc] = useState('234234234');
  const [cover, setCover] = useState('');
  const [avatarModal, setAvatarModal] = useState(true);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  console.log(cover);
  const onFileChange1 = async e => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      console.log(file);
      let imageDataUrl = await readFile(file);
      setImageSrc(imageDataUrl);
    }
  };
  const handleSubm = async () => {
    if (imageSrc && croppedAreaPixels) {
      const croppedImage = await getCroppedImg64(imageSrc, croppedAreaPixels, rotation);
      setCover(croppedImage);
    }
    setAvatarModal(false);
  };
  const onZoomChange = zoom => {
    setZoom(zoom);
  };

  const onCropComplete = async (croppedArea, croppedAreaPixels) => {
    try {
      if (imageSrc) {
        setCroppedAreaPixels(croppedAreaPixels); // Установите croppedAreaPixels
      }
    } catch (error) {
      console.error('Ошибка при обрезке изображения:', error);
    }
  };
  const handleRotation = () => {
    if (rotation === 0) {
      setRotation(90);
    }
    if (rotation === 90) {
      setRotation(180);
    }
    if (rotation === 180) {
      setRotation(270);
    }
    if (rotation === 270) {
      setRotation(0);
    }
  };
  return (
    <>
      <Navbar />
      {imageSrc && avatarModal && (
        <div className="h-[100%] z-[9999]  ">
          <div className="w-[100%]  h-[100%] crop_cont  absolute left-0">
            <div
              className="w-[94%] rounded-[24px] h-[600px] top-[5%] absolute left-0 bg-white z-[9999]"
              style={{ left: '50%', transform: 'translateX(-50%)' }}
            >
              <h1 className="relative text-center top-[2.4%] font-[500] text-[18px]">
                Обрежьте фото
              </h1>
              <div className="flex justify-center w-[100%] my-6 text-center">
                <span className="text-[14px] max-w-[90%]   relative inline-block">
                  Кадрируйте изображения как вам удобно и сохраните его как обложку вашей
                  страницы
                </span>
              </div>
              <div
                style={{
                  position: 'absolute',
                  top: '28%',
                  left: '50%',
                  width: '94%',
                  height: '30%',
                  transform: 'translateX(-50%)',
                }}
              >
                <Cropper
                  image={imageSrc}
                  crop={crop}
                  zoom={zoom}
                  aspect={4 / 4}
                  onCropChange={setCrop}
                  onCropComplete={onCropComplete}
                  onZoomChange={onZoomChange}
                  cropShape="round"
                  rotation={rotation}
                />
              </div>
              <button
                className="absolute  text-primary text-[16px] font-[500]   z-[99999] rounded-[20px] flex justify-center items-center underline"
                style={{
                  top: '65%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                }}
                onClick={handleRotation}
              >
                Перевернуть на 90°
              </button>
              <button
                className="absolute bg-primary text-[18px] font-[500] text-white  z-[99999] rounded-[10px] w-[80%] h-[55px] flex justify-center items-center"
                style={{
                  top: '74%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                }}
                onClick={handleSubm}
              >
                Сохранить изменения
              </button>
              <button
                className="absolute bg-white text-[18px] font-[500] text-primary border-[2px]  z-[99999] rounded-[10px] w-[80%] h-[55px] flex justify-center items-center"
                style={{
                  top: '86%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                }}
                onClick={handleSubm}
              >
                Отменить изменения
              </button>
            </div>
          </div>
        </div>
      )}
      <div className="App mx-[4%] my-6">
        <div className="text-left w-[100%] text-gray-900 text-[25px] font-extrabold font-['Manrope'] leading-[37.50px]">
          Админ-панель
        </div>
        <div className="flex">
          <div className="w-[100%] flex items-left my-8">
            <input
              className="w-[425px] h-[47px] p-2.5 bg-white rounded-[10px]   inline-flex pl-8"
              placeholder="Найти студента"
            />
            <button className="w-[99px] h-[47px] px-5 py-2.5 bg-blue-500 rounded-[10px] justify-center items-center gap-3 inline-flex mx-4">
              <div className="text-white text-xl font-normal font-['Manrope']">Поиск</div>
            </button>
            <button className="w-[188px] h-[47px] px-5 py-2.5 rounded-[10px] border border-blue-500 justify-start items-center gap-2 inline-flex">
              <div className="min-w-[15px] h-[15px] rounded-full border border-blue-500"></div>
              <div className="text-center w-[188px] text-blue-500 text-xl font-normal font-['Manrope']">
                Выбрать все
              </div>
            </button>
          </div>
          <div className="w-[100%] flex justify-end  my-8 ">
            <button class="w-[138px] h-[47px] px-[30px] py-2.5 bg-red-500 rounded-[10px] justify-start items-center gap-2 inline-flex">
              <div class="text-center text-white text-xl font-normal font-['Manrope']">
                Удалить
              </div>
            </button>
            <button
              onClick={() => setAddModal(true)}
              class="w-[153px] h-[47px] px-[30px] py-2.5 bg-blue-500 rounded-[10px] justify-start items-center gap-2 inline-flex mx-2"
            >
              <div class="text-center text-white text-xl font-normal font-['Manrope']">
                Добавить
              </div>
            </button>
          </div>
        </div>
        <div class="w-[100%] h-[30px] justify-start items-start gap-[113px] inline-flex ml-[6%] my-2">
          <div class="w-[133px] text-gray-900 text-xl font-semibold font-['Manrope'] leading-[30px]">
            Фамилия Имя
          </div>
          <div class="w-[133px] text-gray-900 text-xl font-semibold font-['Manrope'] leading-[30px]">
            Направление
          </div>
          <div class="w-[169px] text-gray-900 text-xl font-semibold font-['Manrope'] leading-[30px]">
            Поиск работы
          </div>
        </div>
      </div>
    </>
  );
}
function readFile(file) {
  return new Promise(resolve => {
    const reader = new FileReader();
    reader.addEventListener('load', () => resolve(reader.result), false);
    reader.readAsDataURL(file);
  });
}

export default AdminPage;
