import { useEffect, useState } from 'react';
import AddModal from '../AddModal/AddModal';
import Navbar from '../Navbar';
import getCroppedImg64 from '../getImage64';
import Cropper from 'react-easy-crop';
import CustomCheckbox from '../CustomCheckbox';

function AdminPage() {
  const [column, setColumn] = useState(true);

  const onlyCol = () => {
    setColumn(false);
  };
  const [checkboxes, setCheckboxes] = useState([
    { id: 1, label: 'Бизнес-информатика', isChecked: false },
    { id: 2, label: 'Программная инженерия', isChecked: false },
  ]);

  const handleCheckboxChange = id => {
    setCheckboxes(prevCheckboxes =>
      prevCheckboxes.map(checkbox =>
        checkbox.id === id ? { ...checkbox, isChecked: !checkbox.isChecked } : checkbox
      )
    );
  };
  const [students, setStudents] = useState([]);
  console.log(students);
  const [selectAll, setSelectAll] = useState(false);
  const [selectedStudents, setSelectedStudents] = useState([]);
  console.log(selectedStudents);

  const toggleCheckbox = studentId => {
    if (selectedStudents.includes(studentId)) {
      setSelectedStudents(selectedStudents.filter(id => id !== studentId));
    } else {
      setSelectedStudents([...selectedStudents, studentId]);
    }
  };

  const toggleSelectAll = () => {
    if (selectAll) {
      // Если уже выбраны все, снимаем выбор со всех студентов
      setSelectedStudents([]);
    } else {
      // Иначе выбираем всех студентов
      setSelectedStudents(students.map(student => student._id));
    }
    // Инвертируем состояние выбора всех чекбоксов
    setSelectAll(!selectAll);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4000/service');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        // Фильтрация пустых или неполных записей
        const filteredStudents = data.students.filter(student => student.name);
        setStudents(filteredStudents);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    return () => {
      // Cancel the request here if needed
    };
  }, []);
  const [isChecked, setIsChecked] = useState(true);

  console.log(isChecked);

  const [searchValue, setSearchValue] = useState('');

  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchValue.toLowerCase())
  );

  const handleSearchChange = event => {
    setSearchValue(event.target.value);
  };

  const deleteStudents = async () => {
    try {
      const response = await fetch('http://localhost:4000/service/only', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: selectedStudents }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to delete students');
      }
      const data = await response.json();
      console.log(data);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const [addModal, setAddModal] = useState(false);

  useEffect(() => {
    // Проверяем значение avatar и устанавливаем соответствующий стиль для body
    if (addModal) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'visible';
    }
  }, [addModal]);

  return (
    <>
      {addModal && <AddModal setAddModal={setAddModal} />}
      <Navbar />

      <div className="App w-[100%] ">
        <div className="relative">
          <img src="Chesu.png" className="w-full h-auto" alt="Chesu" />
          <div className="absolute top-0 left-[5%] w-[52%] h-full flex flex-col justify-center items-left">
            <span className="text-white text-left text-3xl font-extrabold font-manrope mb-4">
              Портфолио студентов Чеченского государственного университет им. А.А.
              Кадырова
            </span>
            <span className="text-white w-[60%] text-left text-[15px]">
              Данный сервис предназначен для работодателей, которые ищут для найма
              студентов-выпускников нашего ВУЗа
            </span>
          </div>
        </div>
        <div className="flex w-[100%]">
          <div class="w-[30%] min-h-[1020px] relative  pb-[30px] bg-white flex-col justify-start items-start gap-5 inline-flex">
            <div className="flex w-[94%] pl-[4%] pr-[2%] pt-5 justify-between">
              <div>
                <span className="text-xl font-bold">Фильтры</span>
              </div>
              <div>
                <span className=" text-blue-500">Очистить все</span>
              </div>
            </div>
            <div className="w-[100%]">
              <hr
                style={{ borderColor: '#6F767E' }}
                className="w-[94%] flex justify-center h-[0px]"
              />
            </div>
            <div className="w-[100%] pl-[4%] pr-[2%] text-left">
              <span class=" text-gray-900 text-[16px] font-bold font-['Manrope']">
                Факультет/Направление{' '}
              </span>
            </div>
            <div className="w-[100%] pl-[4%] pr-[2%] flex justify-left ">
              <input
                placeholder="Найти нужное направление "
                class="w-[90%] text-[14px] pl-[6%] h-[44px] pl-[15px] pr-[70px] py-[9px] bg-slate-50 rounded-[7px] justify-center items-center gap-2.5 inline-flex"
              />
            </div>
            <div className="flex-col text-left w-[100%] pr-[2%] pl-[4%] ">
              <button className="flex  my-2 justify-between w-[90%]">
                <span>Агротехнологический институт</span>
                <img className="w-[20px]" src="CaretDown.svg" />
              </button>
              <div className="flex items-center mx-5 my-5 ">
                {checkboxes.map(checkbox => (
                  <CustomCheckbox
                    key={checkbox.id}
                    label={checkbox.label}
                    isChecked={checkbox.isChecked}
                    onChange={() => handleCheckboxChange(checkbox.id)}
                  />
                ))}
              </div>
            </div>
          </div>
          <div className="w-[100%] flex-col">
            <div className="w-[92.8%] flex ml-[3%] my-[2%]">
              <div className="w-[80%] text-left ">
                <span class="w-[100%] text-left text-gray-900 text-[25px] font-extrabold font-['Manrope'] leading-[37.50px]">
                  Найдено 8 студентов
                </span>
              </div>
              <div className="flex  ">
                <button
                  onClick={() => setColumn(true)}
                  className="w-[48px] h-[48px] bg-customWhite flex justify-center items-center rounded-[10px] "
                >
                  {column ? (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="23"
                      height="23"
                      viewBox="0 0 23 23"
                      fill="none"
                    >
                      <rect width="10.3103" height="10.3103" rx="4" fill="#2A85FF" />
                      <rect
                        y="12.6897"
                        width="10.3103"
                        height="10.3103"
                        rx="4"
                        fill="#2A85FF"
                      />
                      <rect
                        x="12.6895"
                        width="10.3103"
                        height="10.3103"
                        rx="4"
                        fill="#2A85FF"
                      />
                      <rect
                        x="12.6895"
                        y="12.6897"
                        width="10.3103"
                        height="10.3103"
                        rx="4"
                        fill="#2A85FF"
                      />
                    </svg>
                  ) : (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="23"
                      height="23"
                      viewBox="0 0 19 19"
                      fill="none"
                    >
                      <rect
                        x="0.791992"
                        y="0.791672"
                        width="7.16236"
                        height="7.16236"
                        rx="2.66667"
                        stroke="#130F26"
                      />
                      <rect
                        x="0.791992"
                        y="10.8376"
                        width="7.16236"
                        height="7.16236"
                        rx="2.66667"
                        stroke="#130F26"
                      />
                      <rect
                        x="10.8379"
                        y="0.791672"
                        width="7.16236"
                        height="7.16236"
                        rx="2.66667"
                        stroke="#130F26"
                      />
                      <rect
                        x="10.8379"
                        y="10.8376"
                        width="7.16236"
                        height="7.16236"
                        rx="2.66667"
                        stroke="#130F26"
                      />
                    </svg>
                  )}
                </button>
                <button
                  onClick={() => setColumn(false)}
                  className="w-[48px] mx-3 h-[48px] bg-customWhite flex justify-center items-center rounded-[10px]"
                >
                  {column ? (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="21"
                      height="14"
                      viewBox="0 0 21 14"
                      fill="none"
                    >
                      <line y1="1" x2="21" y2="1" stroke="#130F26" stroke-width="2" />
                      <line y1="7" x2="17" y2="7" stroke="#130F26" stroke-width="2" />
                      <line y1="13" x2="12" y2="13" stroke="#130F26" stroke-width="2" />
                    </svg>
                  ) : (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="21"
                      height="14"
                      viewBox="0 0 21 14"
                      fill="none"
                    >
                      <line y1="1" x2="21" y2="1" stroke="#2A85FF" stroke-width="2" />
                      <line y1="7" x2="17" y2="7" stroke="#2A85FF" stroke-width="2" />
                      <line y1="13" x2="12" y2="13" stroke="#2A85FF" stroke-width="2" />
                    </svg>
                  )}
                </button>
              </div>
              <div>
                <selector className="w-[281px] h-12 p-3 bg-white rounded-[10px] shadow justify-between items-center inline-flex">
                  <option>выбор</option>
                </selector>
              </div>
            </div>
            {column && (
              <div className="w-[100%] flex flex-wrap relative left-[3%]  ">
                {students?.map(student => (
                  <div className="w-[22%] mr-4 h-[200px] rounded-[10px] my-2 bg-customWhite flex justify-center">
                    <div className="flex-col ">
                      <div className="flex  my-6">
                        <div className="bg-slate-50 w-[60px] h-[60px] flex justify-center items-center rounded-[360px]">
                          <img className="w-[30px] h-[30px]" src="Layer_1.svg" />
                        </div>
                        <div className="flex-col ml-3 text-left">
                          <div class="text-gray-900 text-[15px] font-extrabold font-['Manrope']">
                            {student?.name}
                          </div>
                          <div class="text-gray-500 text-[12px]  font-normal font-['Manrope']">
                            {student?.direction}
                          </div>
                          <div class="w-[100px] h-5 px-[15px] py-[3px] bg-red-500 rounded-xl flex-col justify-center items-center gap-2.5 inline-flex">
                            <div class="self-stretch text-center text-white text-[8px] font-normal font-['Manrope']">
                              Не ищет работу
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="flex">
                        {JSON.parse(student.skills[0])
                          .slice(0, 3)
                          .map((skills, index) => (
                            <div
                              key={index}
                              className="w-[22%] mr-2 h-6 px-2.5 py-[7px] bg-blue-500 bg-opacity-20 rounded-[15px] justify-center items-center gap-2.5 inline-flex"
                            >
                              <div className="text-blue-500 text-[7px] font-normal font-['Manrope']">
                                {skills}
                              </div>
                            </div>
                          ))}
                        {JSON.parse(student.skills[0]).length > 3 && (
                          <div class="w-[30px] h-[25px] px-2.5 py-[7px] rounded-[15px] border border-blue-500 justify-center items-center gap-2.5 inline-flex">
                            <span class="text-blue-500 text-[8px] font-semibold font-['Manrope']">
                              +{JSON.parse(student.skills[0]).length - 4}
                            </span>
                          </div>
                        )}
                      </div>
                      <div className="w-[100%] my-3">
                        <button className="w-[100%] h-[30px] px-[25px] py-2 bg-blue-500 rounded-[5px] justify-center items-center gap-2.5 inline-flex">
                          <div class="text-center text-white text-[10px] font-normal font-['Manrope']">
                            Подробнее
                          </div>
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
            {!column && (
              <div className="w-[100%] flex-col  relative left-[3%]  ">
                {students?.map(student => (
                  <div className="w-[92%]  h-[100px] rounded-[10px] my-6 bg-customWhite flex justify-center">
                    <div className="flex w-[90%] justify-between items-center ">
                      <div className="flex i  my-6">
                        <div className="bg-slate-50 w-[60px] h-[60px] flex justify-center items-center rounded-[360px]">
                          <img className="w-[30px] h-[30px]" src="Layer_1.svg" />
                        </div>
                        <div className="flex-col  items-left ml-3 text-left">
                          <div class="text-gray-900 text-[15px] font-extrabold font-['Manrope']">
                            {student?.name}
                          </div>
                          <div class="text-gray-500 text-[12px] font-normal font-['Manrope']">
                            {student?.direction}
                          </div>
                          <div class="w-[100px] h-5 px-[15px] py-[3px] bg-red-500 rounded-xl flex-col justify-center items-center gap-2.5 inline-flex">
                            <div class="self-stretch text-center text-white text-[8px] font-normal font-['Manrope']">
                              Не ищет работу
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="flex">
                        {JSON.parse(student.skills[0])
                          .slice(0, 3)
                          .map((skills, index) => (
                            <div
                              key={index}
                              className="w-[56.67px] h-[30px] p-2.5 bg-blue-500 bg-opacity-20 rounded-[20px] justify-center items-center gap-2.5 inline-flex ml-2"
                            >
                              <div className="text-blue-500 text-[9px] font-normal font-['Manrope']">
                                {skills}
                              </div>
                            </div>
                          ))}
                        {JSON.parse(student.skills[0]).length > 3 && (
                          <div class="w-[38px] h-[30px] px-2.5 py-[7px] rounded-[15px] border border-blue-500 justify-center ml-2 items-center gap-2.5 inline-flex">
                            <span class="text-blue-500 text-[8px] font-semibold font-['Manrope']">
                              +{JSON.parse(student.skills[0]).length - 4}
                            </span>
                          </div>
                        )}
                      </div>
                      <div className="w-[20%] flex my-3">
                        <button class="w-[100%] h-[30px] px-[25px] py-2 bg-blue-500 rounded-lg flex-col justify-center items-center gap-2.5 inline-flex">
                          <div class="text-center text-white text-[10px] font-normal font-['Manrope']">
                            Подробнее
                          </div>
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default AdminPage;
